INSERT INTO quote(sentences,number_of_people) VALUES ('<b>[p1]:</b> What''s worse than a heartbreak? <br> <b>[p2]:</b> Waking up in the morning and your phone wasn''t charging. <br> <b>[p3]:</b> Waking up in the morning. <br> <b>[p4]:</b> Waking up. <br> <b>[p5]:</b> Waking up in the morning... <br> <b>[p5]:</b> And seeing [p6]. <br> <b>[p6]:</b> Hey! Rude!!',6);
INSERT INTO quote(sentences,number_of_people) VALUES ('<b>[p1]:</b> A mouse! <br> <b>[p2], pulling out a broom:</b> Go back to where you came from or I''ll hit you. <br> <b>[p3], pulling out a frying pan:</b> It''ll make a nice meal! <br> <b>[p4], giving the mouse cheese:</b> You deserve a treat, little guy. <br> <b>[p5], gasping:</b> It''s Ratatouille! <br> <b>[p6]:</b> His name is Remi, dummy. <br> <b>[p1]:</b> ...I was going to say to just trap it and throw it out the window... what is wrong with you people.',6);
INSERT INTO quote(sentences,number_of_people) VALUES ('*The Squad is on the bus, and a child is crying* <br> <b>[p1]:</b> *rolls eyes to the sky* <br> <b>[p2]:</b> *makes funny faces to get them to stop* <br> <b>[p3]:</b> *puts their earphones on at 100% volume* <br> <b>[p4]:</b> *doesn''t mind, doesn''t bother* <br> <b>[p5]:</b> *is the reason they''re crying* <br> <b>[p6]:</b> *enjoys in silence*',6);
INSERT INTO quote(sentences,number_of_people) VALUES ('<b>[p1]:</b> Wait. Where''s [p2]? They love Dungeons and Dragons. <br> <b>[p3]:</b> I thought you invited them. <br> <b>[p4]:</b> Uh, I thought [p5] invited them. <br> <b>[p5]:</b> I thought [p5] invited them. <br> <b>[p5]:</b> I never invite them.',6);